    'storageEndpoint',
    'galleryEndpointUrl',
    'activeDirectoryGraphResourceId',
    'keyVaultDnsSuffix',
    'azureDataLakeStoreFileSystemEndpointSuffix',
    'azureDataLakeAnalyticsCatalogAndJobEndpointSuffix'
